mapper.select:
SELECT * FROM ( SELECT rownum rnum, B.* FROM ( SELECT boardno, tag, title, writer, hit, writtendate FROM freeboard_kg
WHERE ${searchType } LIKE '%' || ${keyword } || '%'
ORDER BY boardno DESC ) B ORDER BY rnum ) RB WHERE rnum BETWEEN #{startNo } AND #{endNo }

mapper.update:
UPDATE freeboard_kg
SET hit=hit+1
WHERE boardno=#{boardno }

UPDATE freeboard_kg
SET tag=#{tag }, title=#{title }, content=#{content }
WHERE boardno=#{boardno }

mapper.select:
SELECT * FROM freecomment_kg
WHERE boardno=#{boardno }
ORDER BY commentno DESC

mapper.update:
UPDATE freeboard_notice_kg
SET hit=hit+1
WHERE boardno=#{boardno }

UPDATE freeboard_notice_kg
SET tag=#{tag }, title=#{title }, content=#{content }
WHERE boardno=#{boardno }
