mapper.select:
SELECT COUNT(*) FROM member_kg
WHERE email = #{email } AND
password = #{password }

	SELECT 
    	boardno, tag, title, product,
	    content, writer, hit, price,
	    writtendate
    FROM (
    	SELECT rownum rnum, B.* FROM (
    	    SELECT * FROM UsedBoard_KG
    	    ORDER BY boardno DESC
    	    ) B
    	    WHERE writer = #{keyword }
    	ORDER BY rnum
    ) UsedBoard_KG
    WHERE rnum BETWEEN #{startNo } AND #{endNo }

SELECT * FROM ( SELECT rownum rnum, B.* FROM ( SELECT boardno,tt.lecture_section,tt.lecture_name,tt.professor_name, team_project,homework,total_score,writer,hit,writtendate FROM lectureboard_kg lb, timetable_kg tt WHERE lb.department_code=tt.lecture_code
AND writer LIKE '%' || '${keyword }' || '%'
ORDER BY boardno DESC ) B ORDER BY rnum ) RB WHERE rnum BETWEEN #{startNo } AND #{endNo }

mapper.update:
		update Member_kg set
  		password = #{password}
 		where hakbun = #{hakbun}

		update Member_kg set
  		nickname = #{nickname}
 		where hakbun = #{hakbun}

 		update tastyboard_kg set
 		writer = #{newnickname}
		WHERE writer = #{oldnickname}

 		update freeboard_kg set
 		writer = #{newnickname}
		WHERE writer = #{oldnickname}

 		update usedboard_kg set
 		writer = #{newnickname}
		WHERE writer = #{oldnickname}

 		update lectureboard_kg set
 		writer = #{newnickname}
		WHERE writer = #{oldnickname}

